            {
                kodGL = "0";
            }

            GLCodeList = new SelectList(db.tbl_SAPGLPUP.Where(x => x.fld_NegaraID == NegaraID && x.fld_SyarikatID == SyarikatID && x.fld_Deleted == false).DistinctBy(x => x.fld_GLCode).OrderBy(o => o.fld_GLCode)
