        [HttpPost]
        [ValidateAntiForgeryToken]
        public ActionResult _BlokTotalDaysWorkingMaintenanceEdit(ModelsCorporate.tblOptionConfigsWebBlokUpdate optionConfigsWeb)
        {
            int? NegaraID, SyarikatID, WilayahID, LadangID = 0;
            int? getuserid = GetIdentity.ID(User.Identity.Name);
            string host, catalog, user, pass = "";
            GetNSWL.GetData(out NegaraID, out SyarikatID, out WilayahID, out LadangID, getuserid, User.Identity.Name);


            try
            {
                if (ModelState.IsValid)
                {
                    var unitData = db.tblOptionConfigsWebs.SingleOrDefault(
                        x => x.fldOptConfID == optionConfigsWeb.fldOptConfID && x.fldOptConfFlag1 == "totaldaysworking" &&
                             x.fld_NegaraID == NegaraID &&
                             x.fld_SyarikatID == SyarikatID);


                    int no = Convert.ToInt32(unitData.fldOptConfValue);
                    int max = Convert.ToInt32(unitData.fldOptConfFlag2);

                    if (no > max)
                    {
                        return Json(new
                        {
                            success = false,
                            msg = GlobalResCorp.lblError,
                            status = "danger",
                            checkingdata = "0"
                        });
                    }

                    else
                    {

                        unitData.fldOptConfValue = optionConfigsWeb.fldOptConfValue.ToUpper();
                        unitData.fldOptConfDesc = GetConfig.UppercaseFirst(optionConfigsWeb.fldOptConfDesc);

                        db.SaveChanges();

                        string appname = Request.ApplicationPath;
                        string domain = Request.Url.GetLeftPart(UriPartial.Authority);
                        var lang = Request.RequestContext.RouteData.Values["lang"];

                        if (appname != "/")
                        {
                            domain = domain + appname;
                        }

                        return Json(new
                        {
                            success = true,
                            msg = GlobalResCorp.msgUpdate,
                            status = "success",
                            checkingdata = "0",
                            method = "1",
                            div = "BlokTotalDaysWorkingMaintenanceDetails",
                            rootUrl = domain,
                            action = "_BlokTotalDaysWorkingMaintenance",
                            controller = "Maintenance"
                        });
                    }
                }

                else
                {
                    return Json(new
                    {
                        success = false,
                        msg = GlobalResCorp.msgErrorData,
                        status = "danger",
                        checkingdata = "0"
                    });
                }
            }

            catch (Exception ex)
            {
                geterror.catcherro(ex.Message, ex.StackTrace, ex.Source, ex.TargetSite.ToString());
                return Json(new
                {
                    success = false,
                    msg = GlobalResCorp.msgError,
                    status = "danger",
                    checkingdata = "0"
                });
            }

            finally
            {
                db.Dispose();
            }
        }

