        public ActionResult _PayrateGMNMaintenance(string WlyhList, string CategoryList, int page = 1, string sort = "fld_KodAktvt", string sortdir = "ASC")
        {
            //ViewBag.Maintenance = "class = active";
            int? getuserid = GetIdentity.ID(User.Identity.Name);
            int? NegaraID, SyarikatID, WilayahID, LadangID = 0;
            GetNSWL.GetData(out NegaraID, out SyarikatID, out WilayahID, out LadangID, getuserid, User.Identity.Name);

            MVC_SYSTEM_Viewing dbview = new MVC_SYSTEM_Viewing();
            int pageSize = int.Parse(GetConfig.GetData("paging"));
            var records = new PagedList<ModelsCorporate.tbl_UpahAktiviti>();
            if (WlyhList != "0" && WlyhList != null)
            {
                int selectedWlyh = int.Parse(WlyhList);
                if (CategoryList != "0")
                {
                    records.Content = db.tbl_UpahAktiviti.Where(x => x.fld_KategoriAktvt == CategoryList && x.fld_SyarikatID == SyarikatID && x.fld_NegaraID == NegaraID && x.fld_WilayahID == selectedWlyh)
                           .OrderBy(sort + " " + sortdir)
                           .Skip((page - 1) * pageSize)
                           .Take(pageSize)
                           .ToList();

                    records.TotalRecords = db.tbl_UpahAktiviti.Where(x => x.fld_KategoriAktvt == CategoryList && x.fld_SyarikatID == SyarikatID && x.fld_NegaraID == NegaraID && x.fld_WilayahID == selectedWlyh).Count();
                    records.CurrentPage = page;
                    records.PageSize = pageSize;
                    //ViewBag.AktvtList = AktvtList;
                    db.Dispose();
                    dbview.Dispose();
                    ViewBag.Datacount = records.TotalRecords;
                }
                else
                {
                    records.Content = db.tbl_UpahAktiviti.Where(x => x.fld_SyarikatID == SyarikatID && x.fld_NegaraID == NegaraID)
                           .OrderBy(sort + " " + sortdir)
                           .Skip((page - 1) * pageSize)
                           .Take(pageSize)
                           .ToList();

                    records.TotalRecords = db.tbl_UpahAktiviti.Where(x => x.fld_SyarikatID == SyarikatID && x.fld_NegaraID == NegaraID).Count();
                    records.CurrentPage = page;
                    records.PageSize = pageSize;
                    db.Dispose();
                    dbview.Dispose();
                    ViewBag.Datacount = records.TotalRecords;
                }
            }
            else
            {
                ViewBag.Datacount = 0;
            }




            //ViewBag.AktvtList = aktvtlist;

            return View(records);

        }

