        public ActionResult _PupGLMapping(string filter, int page = 1, string sort = "fld_Paysheet", string sortdir = "ASC")
        {
            int? NegaraID, SyarikatID, WilayahID, LadangID = 0;
            int? getuserid = GetIdentity.ID(User.Identity.Name);
            string host, catalog, user, pass = "";
            GetNSWL.GetData(out NegaraID, out SyarikatID, out WilayahID, out LadangID, getuserid, User.Identity.Name);

            int pageSize = int.Parse(GetConfig.GetData("paging"));
            var records = new PagedList<ModelsCorporate.tbl_MapGL>();
            int role = GetIdentity.RoleID(getuserid).Value;

            var AktEstData = db.tbl_MapGL.Where(x => x.fld_NegaraID == NegaraID && x.fld_SyarikatID == 1);

            if (!String.IsNullOrEmpty(filter))
            {
                records.Content = AktEstData
                    .Where(x => x.fld_KodAktvt.ToUpper().Contains(filter.ToUpper()) || x.fld_KodGL.ToUpper().Contains(filter.ToUpper()))
                    .OrderBy(sort + " " + sortdir)
                    .Skip((page - 1) * pageSize)
                    .Take(pageSize)
                    .ToList();

                records.TotalRecords = AktEstData
                    .Count(x => x.fld_KodAktvt.ToUpper().Contains(filter.ToUpper()) || x.fld_KodGL.ToUpper().Contains(filter.ToUpper()));
            }
            else
            {
                records.Content = AktEstData.OrderBy(sort + " " + sortdir)
                    .Skip((page - 1) * pageSize)
                    .Take(pageSize)
                    .ToList();

                records.TotalRecords = AktEstData.Count();
            }

            records.CurrentPage = page;
            records.PageSize = pageSize;
            ViewBag.RoleID = role;
            ViewBag.pageSize = 1;

            return View(records);
        }

