                    .Where(x => x.fld_NNCC.ToUpper().Contains(filter.ToUpper()) || x.fld_KodAktivitiOPMS.ToUpper().Contains(filter.ToUpper()))
                    .OrderBy(sort + " " + sortdir)
                    .Skip((page - 1) * pageSize)
                    .Take(pageSize)
                    .ToList();

